'use strict';

import 'jquery';
import 'underscore';
import 'bootstrap';

import angular from 'angular';
import 'angular-ui-router';

// CSS
import 'font-awesome/css/font-awesome.min.css!';
import 'bootstrap/css/bootstrap.min.css!';
import 'bootstrap/css/bootstrap-flex.min.css!';

// Import our app config files
import {AppConstants, ConfigRoutes, ConfigStateWithScope} from './app/config/index';

// Import our templates file (generated by Gulp)
import './app/config/app.templates';

// Import our app functionality
import {ServiceModule} from './app/services/index';
import './app/layout/index';
import './app/components/index';
import './app/auth/index';
import './app/home/index';
import './app/order/index';
import './app/pizza/index';

const requires = [
  'ui.router', 'templates',
  'app.services', 'app.components', 'app.auth', 'app.layout', 'app.home', 'app.order', 'app.pizza'];


export default 
angular.module('seeder', requires)
       .constant('AppConstants', AppConstants)
       .config(ConfigRoutes)
       .run(ConfigStateWithScope)
       .name;

angular
    .element(document)
    .ready(function() {
        angular.bootstrap(document, ['seeder']);
    });
